CREATE TABLE `reservanatural`.`flora` (
  `idflora` INT NOT NULL AUTO_INCREMENT,
  `Nombre` VARCHAR(200) NULL,
  `Imagen` VARCHAR(500) NULL,
  `Descripcion` VARCHAR(50000) NULL,
  PRIMARY KEY (`idflora`));



CREATE TABLE `reservanatural`.`contactenos` (
  `idcontactenos` INT NOT NULL AUTO_INCREMENT,
  `Nombre` VARCHAR(45) NULL,
  `Telefono` INT NULL,
  `Correo` VARCHAR(45) NULL,
  PRIMARY KEY (`idcontactenos`));
  

